open hana9_order

--
-- 受注余力があって、まだ受注していない店舗がいくつかある状態
--
pred allDetailOK {
  some o: Order | 
    (o.order_details.requested_deliveries = none) 
    && gt[#o.order_details, 1]
    && (o.order_details.merchandise.rule_for_ships.shop != none)
    && let candidates = o.order_details.merchandise.rule_for_ships.shop |
      /** 受注候補店舗が3つ以上ある状態を作る。
          同一制約下で、任意の数のatomインスタンスを確保する方策 */
      gt[#candidates,3]
      && all disj candidate1, candidate2, candidate3: candidates |
        canRecieveAllDetail[o,candidate1] 
        && canRecieveAllDetail[o,candidate2] 
        && canRecieveAllDetail[o,candidate3]
}
// run での sig の絞り込みは最小限に。また、
// 全体のscope数と大きく差のあるsig絞り込みを設定すると、正しく動作しないことがある。
// なおこのalsは alloy* (alloystar) での実行を前提としている。
run {
  allDetailOK
} for 8 but 3 ShipLimit, // 4 Merchandise, 5 City, 
1..20 Int, 20 seq
//5 Int, 15 seq

module util/integer

/**
 * A collection of utility functions for using Integers in Alloy.
 * Note that integer overflows are silently truncated to the current bitwidth
 * using the 2's complement arithmetic, unless the "forbid overfows" option is
 * turned on, in which case only models that don't have any overflows are 
 * analyzed. 
 */

sig Bit in Int {}

fun BitTrue  : Bit { -1 }
fun BitFalse : Bit { 0 }

fact { Bit = BitFalse + BitTrue }


fun add  [n1, n2: Int] : Int { this/plus[n1, n2] }
fun plus [n1, n2: Int] : Int { n1 fun/add n2 }

fun sub   [n1, n2: Int] : Int { this/minus[n1, n2] }
fun minus [n1, n2: Int] : Int { n1 fun/sub n2 }

fun mul [n1, n2: Int] : Int { n1 fun/mul n2 }

fun bvneg [n: Int]      : Int { fun/bvneg n     }
fun bvnot [n: Int]      : Int { fun/bvnot n     }
fun bvand [n1, n2: Int] : Int { n1 fun/bvand n2 }
fun bvxor [n1, n2: Int] : Int { n1 fun/bvxor n2 }
fun bvor  [n1, n2: Int] : Int { n1 fun/bvor n2  }
fun bvshl [n1, n2: Int] : Int { n1 fun/bvshl n2 }
fun bvshr [n1, n2: Int] : Int { n1 fun/bvshr n2 }
fun bvsha [n1, n2: Int] : Int { n1 fun/bvsha n2 }
pred bveq [n1, n2: Int]       { n1 fun/bveq n2 }

fun bvadd [n1, n2: Int] : Int { this/plus[n1, n2] }
fun bvsub [n1, n2: Int] : Int { this/minus[n1, n2] }
fun bvmul [n1, n2: Int] : Int { this/mul[n1, n2] }
fun bvdiv [n1, n2: Int] : Int { this/div[n1, n2] }

fun Not [n: Int]      : Int { this/bvnot[n] }
fun And [n1, n2: Int] : Int { this/bvand[n1, n2] }
fun Nand[n1, n2: Int] : Int { this/bvnot[this/bvand[n1, n2]] }
fun Or  [n1, n2: Int] : Int { this/bvor[n1, n2] }
fun Nor [n1, n2: Int] : Int { this/bvnot[this/bvor[n1, n2]] }
fun Xor [n1, n2: Int] : Int { this/bvxor[n1, n2] }

/**
 * Performs the division with "round to zero" semantics, except the following 3 cases
 * 1) if a is 0, then it returns 0
 * 2) else if b is 0, then it returns 1 if a is negative and -1 if a is positive
 * 3) else if a is the smallest negative integer, and b is -1, then it returns a
 */
fun div [n1, n2: Int] : Int { n1 fun/div n2 }

/** answer is defined to be the unique integer that satisfies "a = ((a/b)*b) + remainder" */
fun rem [n1, n2: Int] : Int { n1 fun/rem n2 }

/** negate */
fun negate [n: Int] : Int { 0 fun/sub n }

/** equal to */
pred eq [n1, n2: Int] { int[n1] = int[n2] }

/** greater than */
pred gt [n1, n2: Int] { n1 > n2 }

/** less then */
pred lt [n1, n2: Int] { n1 < n2 }

/** greater than or equal */
pred gte [n1, n2: Int] { n1 >= n2 }

/** less than or equal */
pred lte [n1, n2: Int] { n1 <= n2 }

/** integer is zero */
pred zero [n: Int] { n = 0 }

/** positive */
pred pos  [n: Int] { n > 0 }

/** negative */
pred neg  [n: Int] { n < 0 }

/** non-positive */
pred nonpos [n: Int] { n <= 0 }

/** non-negative */
pred nonneg [n: Int] { n >= 0 }

/** signum (aka sign or sgn) */
fun signum [n: Int] : Int { n<0 => (0 fun/sub 1) else (n>0 => 1 else 0) }

/**
 * returns the ith element (zero-based) from the set s
 * in the ordering of 'next', which is a linear ordering
 * relation like that provided by util/ordering
 */
fun int2elem[i: Int, next: univ->univ, s: set univ] : lone s {
  {e: s | #^next.e = int i }
}

/**
 * returns the index of the element (zero-based) in the
 * ordering of next, which is a linear ordering relation
 * like that provided by util/ordering
 */
fun elem2int[e: univ, next: univ->univ] : lone Int {
  Int[#^next.e]
}

/** returns the largest integer in the current bitwidth */
fun max:one Int { fun/max }

/** returns the smallest integer in the current bitwidth */
fun min:one Int { fun/min }

/** maps each integer (except max) to the integer after it */
fun next:Int->Int { fun/next }

/** maps each integer (except min) to the integer before it */
fun prev:Int->Int { ~next }

/** given a set of integers, return the largest element */
fun max [es: set Int]: lone Int { es - es.^prev }

/** given a set of integers, return the smallest element */
fun min [es: set Int]: lone Int { es - es.^next }

/** given an integer, return all integers prior to it */
fun prevs [e: Int]: set Int { e.^prev }

/** given an integer, return all integers following it */
fun nexts [e: Int]: set Int { e.^next }

/** returns the larger of the two integers */
fun larger [e1, e2: Int]: Int { let a=int[e1], b=int[e2] | (a<b => b else a) }

/** returns the smaller of the two integers */
fun smaller [e1, e2: Int]: Int { let a=int[e1], b=int[e2] | (a<b => a else b) }

module hana9

open util/boolean
sig Boundary { val: disj one Int }
one sig Now { val: one Int } { val = 6}

sig City {
  cities_shops: set CitiesShop,
  order_details: set OrderDetail,
  label: one Boundary
}
sig CitiesShop {
  shop: one Shop,
  city: one City
}
sig Merchandise {
  order_details: set OrderDetail,
  rule_for_ships: set RuleForShip,
  label: one Boundary,
  price: one Boundary
}
sig OrderDetail {
  merchandise: one Merchandise,
  city: one City,
  order: one Order,
  requested_deliveries: set RequestedDelivery,
  seq_code: one Boundary,
  expected_date: one Boundary,
  quantity: one Boundary
}
sig Order {
  order_details: set OrderDetail,
  order_code: one Boundary,
  ordered_at: one Boundary
}
sig RequestedDelivery {
  shop: one Shop,
  order_detail: one OrderDetail,
  order_code: one Boundary,
  quantity: one Boundary
}
sig RuleForShip {
  shop: one Shop,
  merchandise: one Merchandise,
  interval_day: one Boundary,
  quantity_limit: one Boundary,
  quantity_available: one Boundary
}
sig ShipLimit {
  shop: one Shop,
  expected_date: one Boundary
}
sig Shop {
  cities_shops: set CitiesShop,
  requested_deliveries: set RequestedDelivery,
  rule_for_ships: set RuleForShip,
  ship_limits: set ShipLimit,
  code: one Boundary,
  label: one Boundary,
  delivery_limit_per_day: one Boundary,
  mergin: one Boundary
}

fact {
  Shop<:cities_shops = ~(CitiesShop<:shop)
  City<:cities_shops = ~(CitiesShop<:city)
  Merchandise<:order_details = ~(OrderDetail<:merchandise)
  City<:order_details = ~(OrderDetail<:city)
  Order<:order_details = ~(OrderDetail<:order)
  Shop<:requested_deliveries = ~(RequestedDelivery<:shop)
  OrderDetail<:requested_deliveries = ~(RequestedDelivery<:order_detail)
  Shop<:rule_for_ships = ~(RuleForShip<:shop)
  Merchandise<:rule_for_ships = ~(RuleForShip<:merchandise)
  Shop<:ship_limits = ~(ShipLimit<:shop)
  all e,e':CitiesShop | e != e' => (e.shop->e.city) != (e'.shop->e'.city)
  all e,e':OrderDetail | e != e' => (e.seq_code->e.order->e.merchandise) != (e'.seq_code->e'.order->e'.merchandise)
  all e,e':Order | e != e' => (e.order_code) != (e'.order_code)
  all e,e':RequestedDelivery | e != e' => (e.shop->e.order_detail) != (e'.shop->e'.order_detail)
  all e,e':RuleForShip | e != e' => (e.shop->e.merchandise) != (e'.shop->e'.merchandise)
  all e,e':ShipLimit | e != e' => (e.shop->e.expected_date) != (e'.shop->e'.expected_date)
  all e,e':Shop | e != e' => (e.code) != (e'.code)
}
